insert into movies (id, description, director, name, poster_uri) values (1, 'After being coerced into working for a crime boss, a young getaway driver finds himself taking part in a heist doomed to fail.
insert into movies (id, description, director, name, poster_uri) values (2, 'A Chicago teenager is looking for fun at home while his parents are away, but the situation quickly gets out of hand.', 'Paul Brickman', 'Risky Business', '/movies/posters/risky_business.jpg');
insert into movies (id, description, director, name, poster_uri) values (3, 'A paranoid, secretive surveillance expert has a crisis of conscience when he suspects that the couple he is spying on will be murdered.', 'Francis Ford Coppola', 'The Conversation', '/movies/posters/the_conversation.jpg');
insert into movies (id, description, director, name, poster_uri) values (4, 'The aging patriarch of an organized crime dynasty transfers control of his clandestine empire to his reluctant son.', 'Francis Ford Coppola', 'The Godfather', '/movies/posters/the_godfather.jpg');
insert into movies (id, description, director, name, poster_uri) values (5, 'The early life and career of Vito Corleone in 1920s New York City is portrayed, while his son, Michael, expands and tightens his grip on the family crime syndicate.', 'Francis Ford Coppola', 'The Godfather II', '/movies/posters/the_godfatherII.jpg');
insert into movies (id, description, director, name, poster_uri) values (6, 'Two imprisoned men bond over a number of years, finding solace and eventual redemption through acts of common decency.', 'Frank Darabont', 'The Shawshank Redemption', '/movies/posters/the_shawshank_redemption.jpg');
insert into movies (id, description, director, name, poster_uri) values (7, 'A former Roman General sets out to exact vengeance against the corrupt emperor who murdered his family and sent him into slavery.', 'Ridley Scott', 'Gladiator', '/movies/posters/gladiator.jpg');
